import reportWebVitals from './reportWebVitals';
const course = 'Half Stack application development'
  const parts = [
    {
      name: 'Fundamentals of React',
      exercises: 10
    },
    {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]
function Header(){
  return(
    <h1>{course}</h1>
  )
}
function Content({part}){
  return(
    <div>
      <p>
        {part[0].name}--{part[0].excercises}
      </p>
      <p>
        {part[1].name}--{part[1].excercises}
      </p>
      <p>
        {part[2].name}--{part[2].excercises}
      </p>
    </div>
  )
}

  function Total({part}){
    var sum=0;
    sum=part[0].exercises+part[1].exercises+part[2].exercises

      return sum
  }


ReactDOM.render(
  <div>
      <Header/>
      <Content part={parts}/>
   <Total part={parts}/>

  </div>






